
public class 변수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       System.out.println("Hello world!!");
       
       
      //불리언 타입 : 참과 거짓을 표현
      // 데이터타입 변수명 = 데이터;
      boolean isApple = true;
      boolean isAdult = false;
      
      // 수치 -> 정수 타입 : 소숫점이 없는 숫자
      byte b = 17;
      short s = 20;
      int i = 1089;
      long a = 16000; 
      char c = 65; // 글자
      
      // 수치 -> 실수 타입 : 소숫점이 있는 숫자
      float f = 3.14f;
      double d = 12.67898;
	
	}

}
